import React, { Component } from 'react';
import axios from 'axios';
import { sortBy } from 'lodash';
import classNames from 'classnames';
import './App.css';

const DEFAULT_USER = 'jl';

const SORTS = {
  NONE: list => list,
  ID: list => sortBy(list, 'id'),
  TYPE: list => sortBy(list, 'type'),
  TIME: list => sortBy(list, 'time'),
  TITLE: list => sortBy(list, 'title'),
  TEXT: list => sortBy(list, 'text'),
  SCORE: list => sortBy(list, 'score'),
  URL: list => sortBy(list, 'url'),
};

class App extends Component {
  _isMounted = false;

  constructor(props) {
    super(props);

    this.state = {
      results: [],
      username: DEFAULT_USER,
      error: null,
      isLoading: false,
      sortKey: 'NONE',
      isSortReverse: false,
    };

    this.needsToGetUserData = this.needsToGetUserData.bind(this);
    this.setUserPost = this.setUserPost.bind(this);
    this.fetchUserData = this.fetchUserData.bind(this);
    this.fetchUserPost = this.fetchUserPost.bind(this);
    this.onSearchChange = this.onSearchChange.bind(this);
    this.onSearchSubmit = this.onSearchSubmit.bind(this);
    this.onDismiss = this.onDismiss.bind(this);
  }

  needsToGetUserData(username) {
    return !this.state.results[username];
  }

  setUserPost(result) {
    const { about, created, id, karma, submitted } = result;
    submitted.map((postID) =>
        this.fetchUserPost(postID)
      );
  }

  fetchUserData(username) {
    this.setState({ isLoading: true });

    axios(`https://hacker-news.firebaseio.com/v0/user/${username}.json`)
      .then(result => this.setUserPost(result.data))
      .catch(error => this._isMounted && this.setState({ error }));
  }

  fetchUserPost(postID) {
    this.setState({ isLoading: true });

    axios(`https://hacker-news.firebaseio.com/v0/item/${postID}.json`)
      .then(result => this.setState({results: this.state.results.concat(result.data), isLoading: false}))
      .catch(error => this._isMounted && this.setState({ error }));
  }

  componentDidMount() {
    this._isMounted = true;

    const { username } = this.state;
    this.setState({ searchKey: username });
    this.fetchUserData(username);
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  onSearchChange(event) {
    this.setState({ username: event.target.value });
  }

  onSearchSubmit(event) {
    const { username } = this.state;
    this.setState({ searchKey: username });

    if (this.needsToGetUserData(username)) {
      this.fetchUserData(username);
    }

    event.preventDefault();
  }

  onDismiss(id) {
    const { searchKey, results } = this.state;
    const { hits, page } = results[searchKey];

    const isNotId = item => item.objectID !== id;
    const updatedHits = hits.filter(isNotId);

    this.setState({
      results: {
        ...results,
        [searchKey]: { hits: updatedHits, page }
      }
    });
  }

  render() {
    const {
      username,
      results,
      error,
      isLoading
    } = this.state;

    // const page = (
    //   results &&
    //   results['text'] &&
    //   results[searchKey].page
    // ) || 0;

    const list = results || [];

    return (
      <div className="page">
        <div className="interactions">
          <Search
            value={username}
            onChange={this.onSearchChange}
            onSubmit={this.onSearchSubmit}
          >
            Submit
          </Search>
        </div>
        { error
          ? <div className="interactions">
            <p>Something went wrong.</p>
          </div>
          : <Table
            list={list}
            onDismiss={this.onDismiss}
          />
        }
        <div className="interactions">
          <ButtonWithLoading
            isLoading={isLoading}
            onClick={() => this.fetchUserData(searchKey, page + 1)}>
            More
          </ButtonWithLoading>
        </div>
      </div>
    );
  }
}

const Search = ({
  value,
  onChange,
  onSubmit,
  children
}) =>
  <form onSubmit={onSubmit}>
    <input
      type="text"
      placeholder="HackerNews username"
      value={value}
      onChange={onChange}
    />
    <button type="submit">
      {children}
    </button>
  </form>

class Table extends Component {
  constructor(props) {
    super(props);

    this.state = {
      sortKey: 'NONE',
      isSortReverse: false,
    };

    this.onSort = this.onSort.bind(this);
  }

  onSort(sortKey) {
    const isSortReverse = this.state.sortKey === sortKey && !this.state.isSortReverse;
    this.setState({ sortKey, isSortReverse });
  }

  render() {
    const {
      list,
      onDismiss
    } = this.props;

    const {
      sortKey,
      isSortReverse,
    } = this.state;

    const sortedList = SORTS[sortKey](list);
    const reverseSortedList = isSortReverse
      ? sortedList.reverse()
      : sortedList;

    return (
      <div className="table">
        <div className="table-header">
          <span style={{ width: '5%' }}>
            <Sort
              sortKey={'ID'}
              onSort={this.onSort}
              activeSortKey={sortKey}
            >
              ID
            </Sort>
          </span>
          <span style={{ width: '10%' }}>
            <Sort
              sortKey={'TYPE'}
              onSort={this.onSort}
              activeSortKey={sortKey}
            >
              Type
            </Sort>
          </span>
          <span style={{ width: '10%' }}>
            <Sort
              sortKey={'TIME'}
              onSort={this.onSort}
              activeSortKey={sortKey}
            >
              Time
            </Sort>
          </span>
          <span style={{ width: '15%' }}>
            <Sort
              sortKey={'TITLE'}
              onSort={this.onSort}
              activeSortKey={sortKey}
            >
              Title
            </Sort>
          </span>
          <span style={{ width: '30%' }}>
            <Sort
              sortKey={'TEXT'}
              onSort={this.onSort}
              activeSortKey={sortKey}
            >
              Text
            </Sort>
          </span>
          <span style={{ width: '10%' }}>
            <Sort
              sortKey={'SCORE'}
              onSort={this.onSort}
              activeSortKey={sortKey}
            >
              Score
            </Sort>
          </span>
          <span style={{ width: '10%' }}>
            <Sort
              sortKey={'URL'}
              onSort={this.onSort}
              activeSortKey={sortKey}
            >
              URL
            </Sort>
          </span>
          <span style={{ width: '10%' }}>
            Bookmarks
          </span>
        </div>
        {reverseSortedList.map(item =>
          <div key={item.id} className="table-row">
            <span style={{ width: '5%' }}>
              {item.id}
            </span>
            <span style={{ width: '10%' }}>
              {item.type}
            </span>
            <span style={{ width: '10%' }}>
              {item.time}
            </span>
            <span style={{ width: '15%' }}>
              <a href={item.url}>{item.title}</a>
            </span>
            <span style={{ width: '30%' }}>
              {item.text}
            </span>
            <span style={{ width: '10%' }}>
              {item.score}
            </span>
            <span style={{ width: '10%' }}>
              {item.url}
            </span>
            <span style={{ width: '10%' }}>
              <Button
                onClick={() => onDismiss(item.objectID)}
                className="button-inline"
              >
                Bookmark
              </Button>
            </span>
          </div>
        )}
      </div>
    );
  }
}

const Sort = ({
  sortKey,
  activeSortKey,
  onSort,
  children
}) => {
  const sortClass = classNames(
    'button-inline',
    { 'button-active': sortKey === activeSortKey }
  );

  return (
    <Button
      onClick={() => onSort(sortKey)}
      className={sortClass}
    >
      {children}
    </Button>
  );
}

const Button = ({
  onClick,
  className = '',
  children,
}) =>
  <button
    onClick={onClick}
    className={className}
    type="button"
  >
    {children}
  </button>

const Loading = () =>
  <div>Loading ...</div>

const withLoading = (Component) => ({ isLoading, ...rest }) =>
  isLoading
    ? <Loading />
    : <Component { ...rest } />

const ButtonWithLoading = withLoading(Button);

export {
  Button,
  Search,
  Table,
};

export default App;
